class VendorFactory(factory.django.DjangoModelFactory):
    class Meta:
        model = 'vendors.Vendor'

    name = factory.Sequence('vendor{}'.format)
    short_name = factory.Sequence('v{}'.format)
    icon = factory.SubFactory('ery_backend.assets.factories.ImageAssetFactory')
    comment = factory.fuzzy.FuzzyText(length=100)
    background_color = factory.fuzzy.FuzzyChoice(hex_colors)
    theme_color = factory.fuzzy.FuzzyChoice(hex_colors)

    @factory.lazy_attribute
    def homepage_url(self):
        return f'{self.name}.ery.sh'
